import React,{useEffect} from 'react'
import {useDispatch,useSelector} from 'react-redux'
import {useHistory} from 'react-router-dom'
import Card from '../../Components/Dashboard/card'
import {getBugs} from '../../../Controllers/Redux/bugSlice'

export default () =>{
    const dispatch = useDispatch();
    const bugs = useSelector(state=>state.bugs);
    const browserHistory = useHistory();
    let highCount = 0;
    let midCount = 0;
    let lowCount = 0;

    if(bugs!=undefined){
        highCount = filterBugs(1);
        midCount = filterBugs(2);
        lowCount = filterBugs(3);
    }
    function redirect(){
        browserHistory.push('/viewbugs');
    }

    function filterBugs(priority){
        return bugs.filter((bug)=>{return bug.priority == priority})
    }
    useEffect(()=>{
        dispatch(getBugs())
    },[bugs==undefined])


return(
    <div className="page-container">
        <Card priority="1" count={highCount.length}   clicked={redirect} />
        <Card priority="2" count={midCount.length} clicked={redirect}/>
        <Card priority="3" count={lowCount.length} clicked={redirect}/>


     <table id="q-graph">
     <caption>Quarterly Results</caption>
     <thead>
     <tr>
     <th></th>
     <th class="sent">Invoiced</th>
     <th class="paid">Collected</th>
     </tr>
     </thead>
     <tbody>
     <tr class="qtr" id="q1">
     <th scope="row">Q1</th>
     <td class="sent bar" style="height: 111px;"><p>$18,450.00</p></td>
     <td class="paid bar" style="height: 99px;"><p>$16,500.00</p></td>
     </tr>
     <tr class="qtr" id="q2">
     <th scope="row">Q2</th>
     <td class="sent bar" style="height: 206px;"><p>$34,340.72</p></td>
     <td class="paid bar" style="height: 194px;"><p>$32,340.72</p></td>
     </tr>
     <tr class="qtr" id="q3">
     <th scope="row">Q3</th>
     <td class="sent bar" style="height: 259px;"><p>$43,145.52</p></td>
     <td class="paid bar" style="height: 193px;"><p>$32,225.52</p></td>
     </tr>
     </tbody>
    </table>
    <div id="ticks">
    <div class="tick" style="height: 59px;"><p>$50,000</p></div>    
    </div>
    </div>

)
}
