import {createSlice} from '@reduxjs/toolkit'

const slice = createSlice({
    name:"auth",
    initialState: {
        LoggedIn:false,
        admin:false,
        role: "",
        userName: "",

    },
    reducers:{
        signIn:(state,action)=>{
            console.log(action.payload)
            const {name,admin,role} = action.payload;
            state.userName = name;
            state.LoggedIn = true;
            state.admin = admin;
            state.role = role;
        },
        signOut:(state)=>{
            state.LoggedIn = false;
            state.admin = false;
        },
        createUser:(state,action)=>{

        }
    }
})

export default slice.reducer;
export const {signIn,signOut,createUser} = slice.actions;
