import React,{useEffect} from 'react'
import {useDispatch,useSelector} from 'react-redux'
import {useHistory} from 'react-router-dom'
import Card from '../../Components/Dashboard/card'
import {getBugs} from '../../../Controllers/Redux/bugSlice'


export default () =>{
    const dispatch = useDispatch();
    const bugs = useSelector(state=>state.bugs);
    const browserHistory = useHistory();
    let highCount = 0;
    let midCount = 0;
    let lowCount = 0;

    if(bugs!=undefined){
        highCount = filterBugs(1);
        midCount = filterBugs(2);
        lowCount = filterBugs(3);
    }


    function redirect(){
        browserHistory.push('/viewbugs');
    }

    function filterBugs(priority){
        return bugs.filter((bug)=>{return bug.priority == priority})
    }
    useEffect(()=>{
        dispatch(getBugs())
    },[bugs==undefined])

    class ApexChart extends React.Component {
        constructor(props) {
          super(props);

          this.state = {

            series: [44, 55, 41, 17, 15],
            options: {
              chart: {
                type: 'donut',
              },
              responsive: [{
                breakpoint: 480,
                options: {
                  chart: {
                    width: 200
                  },
                  legend: {
                    position: 'bottom'
                  }
                }
              }]
            },


          };
        }
    }


return(

        <div className="page-container" >
            <div className="dash" style={{backgroundColor: "#adadad",width: "35%", height: "20%"}}>
                <Card priority="1" count={highCount.length}   clicked={redirect} />
                <Card priority="2" count={midCount.length} clicked={redirect}/>
                <Card priority="3" count={lowCount.length} clicked={redirect}/>

            </div>
            <div id="chart">
            <ReactApexChart options={this.state.options} series={this.state.series} type="donut" />
            </div>


        </div>



)
}
