import React,{useEffect,useState} from 'react'
import {useDispatch, useSelector} from 'react-redux'
import {getBugs} from '../../../Controllers/Redux/bugSlice'
import BugCard from '../../Components/Bug Card/bugCard'
import BugView from '../../Components/Bug view/bugView'
import "./viewBugs.css"

export default() =>{
    let searched = false
    let filterbugs = []

    function search(){
    const searchBar = document.getElementById("search")

    searchBar.addEventListener('keyup',(e)=>{
    const searchString=e.target.value;

    filterbugs=bugs.filter((bug)=>{

        return( bug.name.includes(searchString) )
    })



    })
    }
    const [DISPLAY_BUG,SET_DISPLAY_BUG]= useState({
        name:"",
        isDisplayed:false
    })
    const dispatch = useDispatch();
    const {bugs} = useSelector(state=>state);

    useEffect(()=>{
        dispatch(getBugs());
    },[bugs.length < 1])

    function BugClicked(name){

        SET_DISPLAY_BUG({
            isDisplayed: !DISPLAY_BUG.isDisplayed,
            name: name,
        })

    }
    return(
        <div className="Search">
        <input type="text" id="search" onClick={search} placeholder="Search tickets.."></input>
        <div className='page-container' style={{width:"78%"}}>
            {!searched ? bugs.map((bug,key)=>(
                <BugCard  key={key} bug={bug} clicked={()=>BugClicked(bug.name) } />
            )) :  filterbugs.map((bug,key)=>(
                <BugCard  key={key} bug={bug} clicked={()=>BugClicked(bug.name) } />
            ))}

            {DISPLAY_BUG.isDisplayed && <BugView clicked={BugClicked} bug={bugs.filter((bug) => bug.name == DISPLAY_BUG.name)[0]} />}
        </div>
        </div>
    )
}
