import React,{Component} from 'react';
import { Bar,Line,Doughnut} from 'react-chartjs-2';
import {useSelector} from 'react-redux'


export default()=>{
  const bugs = useSelector(state=>state.bugs);  
  let highCount = 0;
  let midCount = 0;
  let lowCount = 0;
  if(bugs!=undefined){        
      highCount = filterBugs(1).length;
      midCount = filterBugs(2).length;
      lowCount = filterBugs(3).length; 
    }
  function filterBugs(priority){
    return bugs.filter((bug)=>{return bug.priority == priority})
  }
     // useEffect(()=>{
    //     dispatch(getBugs())
    // },[bugs==undefined])

    const chartData={
      labels: ["Low","Medium","High"],
      datasets:[
        {
          label: "Priority",
          data:[lowCount,midCount,highCount],
          backgroundColor:["#b33a3a","#FFA500","#32cd32"]
        }
      ]
    }
   return(
     <div className="bar">
       <Bar
          data={chartData}
          options={{
            title:{
              display:true,
              text:'By prioirity',
              fontSize:25,
            },
            legend:{
              display: true,
              position: 'right',
            }
          }}
        />
     </div>
   )
  }
