import React,{useState} from 'react'

import UserModel from '../../../Models/userModel'
import { useDispatch } from 'react-redux';
import {createUser} from '../../../Controllers/Redux/authSlice'


export default (props)=>{
    const [userObject,setUserObject] = useState(new UserModel(props.user));

    const dispatch = useDispatch();

    function inputChanged(e){
        setUserObject({
            ...userObject,
            [e.target.name]:e.target.value
        })
    }
    function submit(e){
        e.preventDefault()
        dispatch(createUser(userObject))


    }

    return(
        <div className = "container">
            <div className="form-div">
                <form>
                    <input 
                    name="fullName"
                    placeholder="Full Name" 
                    value={userObject.fullName} 
                    required onChange={inputChanged}
                    className="form-control form-group"/>
                    <input 
                    name="userName"
                    placeholder="Username"
                    value={userObject.userName}
                    required onChange={inputChanged}
                    className="form-control form-group"
                    />
                     <input 
                     name="email"
                    placeholder="Email"
                    value={userObject.email}
                    required onChange={inputChanged}
                    className="form-control form-group"
                    />
                     <input 
                     name="password"
                    type="password"
                    placeholder="Password"
                    value={userObject.password}
                    required onChange={inputChanged}
                    className="form-control form-group"
                    />
                    <label>Role: </label>
                    <select name="admin" value={userObject.admin} required onChange={inputChanged}>
                        <option>Admin</option>
                        <option>Client</option>
                    </select>
                    <button type="sumbit" className="btn btn-danger btn-block" onClick={submit} >Create User</button>
                </form>
            </div>

        </div>
    )

}
