import { Bar,Line,Doughnut} from 'react-chartjs-2';
import data from './stats'

export default()=>{

    const chartData={
      labels: ["Low","Medium","High"],
      datasets:[
        {
          label: "Priority",
          data:[data.lowCount,data.midCount,data.highCount],
          backgroundColor:["#b33a3a","#FFA500","#32cd32"]
        }
      ]

    }


   return(
     <div className="bar">

       chart component
       <Bar
          data={chartData}

          options={{ maintainAspectRatio: false }}
        />
     </div>
   )
  }
