import React from 'react';
import { Bar,Line,Doughnut} from 'react-chartjs-2';
import {useSelector} from 'react-redux'
import './chart.css'


export default()=>{
  const bugs = useSelector(state=>state.bugs);  
  let highCount = 0;
  let midCount = 0;
  let lowCount = 0;
  if(bugs!=undefined){        
      highCount = filterBugs(1).length;
      midCount = filterBugs(2).length;
      lowCount = filterBugs(3).length; 
    }
  function filterBugs(priority){
    return bugs.filter((bug)=>{return bug.priority == priority})
  }

    const chartData={

      labels: ["High","Medium","Low"],
      datasets:[
        {
          label: "Priority",
          data:[highCount,midCount,lowCount],
          backgroundColor:["#b33a3a","#FFA500","#32cd32"]
        }
      ]
    }
   return(
     <div >
       <Bar
          data={chartData}
          options={{

            plugins:{
              title:{
                display:true,
                text:'Bugs by prioirity',
                fontSize:'50',
                position:'left',
              },
              legend:{
                display: true,
                position: 'right',
              },
              layout: {
                padding: '50px',
            }
          }}
        }
        />
     </div>
   )
  }
