import React,{useState} from 'react'
import {useDispatch} from 'react-redux'
import {signIn} from '../../Controllers/Redux/authSlice'

import './login.css'
export default()=>{
    const dispatch = useDispatch();

    const [formInput,setFormInput] = useState({
        name:"",
        password:""
    })

    function inputChanged(e){   // when input is chnaged
        setFormInput({
            ...formInput,
            [e.target.name]:e.target.value  //sets the state of the variables
        })
    }

    function submit(e){
        dispatch(signIn(formInput));     //sends state of form to signIn reducer
        e.preventDefault();             //prevents page from automatically reloading after hitting submit
    }

    return(
        <div className="loginBG">

                <form className='login-panel'>
                    <h1>Login</h1>
                    <label> Name
                    <input label name='name' placeholder = 'John Smith' onChange={inputChanged} value={formInput.name}></input>
                    </label>
                    <label>Password
                    <input name='password' type='password' placeholder = '********' onChange={inputChanged} value={formInput.password}></input>
                    </label>
                    <button type='submit' onClick={submit}>Login</button>
                </form>

        </div>
    )
}
